CREATE TABLE location (id INT AUTO_INCREMENT, name VARCHAR(50) DEFAULT '' NOT NULL, description VARCHAR(255) DEFAULT '' NOT NULL, location_type_id INT DEFAULT '0' NOT NULL, lat DOUBLE DEFAULT 0 NOT NULL, lng DOUBLE DEFAULT 0 NOT NULL, created_at DATETIME DEFAULT '1970-01-01 00:00:00' NOT NULL, updated_at DATETIME DEFAULT '1970-01-01 00:00:00' NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE location_type (id INT AUTO_INCREMENT, name VARCHAR(50) DEFAULT '' NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
