CREATE TABLE fish (id BIGINT AUTO_INCREMENT, name VARCHAR(100) NOT NULL UNIQUE, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE friend (id BIGINT UNIQUE, source_profile_id BIGINT NOT NULL, related_profile_id BIGINT NOT NULL, INDEX source_profile_id_idx (source_profile_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE location_version (id INT, name VARCHAR(50) DEFAULT '' NOT NULL, description VARCHAR(255) DEFAULT '' NOT NULL, depth FLOAT(18, 2), location_flow_id INT, location_fundus_id INT, location_relief_id INT, created_by BIGINT NOT NULL, updated_by BIGINT NOT NULL, latitude DOUBLE(18, 2), longitude DOUBLE(18, 2), slug VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, version BIGINT, PRIMARY KEY(id, version)) ENGINE = INNODB;
CREATE TABLE location (id INT AUTO_INCREMENT, name VARCHAR(50) DEFAULT '' NOT NULL, description VARCHAR(255) DEFAULT '' NOT NULL, depth FLOAT(18, 2), location_flow_id INT, location_fundus_id INT, location_relief_id INT, created_by BIGINT NOT NULL, updated_by BIGINT NOT NULL, latitude DOUBLE(18, 2), longitude DOUBLE(18, 2), slug VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, version BIGINT, INDEX location_flow_id_idx (location_flow_id), INDEX location_fundus_id_idx (location_fundus_id), INDEX location_relief_id_idx (location_relief_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE location_flow (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE location_fundus (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE location_relief (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE location_season (id BIGINT AUTO_INCREMENT, location_id INT NOT NULL, season_id INT NOT NULL, INDEX location_id_idx (location_id), INDEX season_id_idx (season_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE profile (id BIGINT AUTO_INCREMENT, nick_name VARCHAR(50) DEFAULT '' NOT NULL, first_name VARCHAR(50) DEFAULT NULL, last_name VARCHAR(50) DEFAULT NULL, description VARCHAR(255) DEFAULT '' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE profit (id BIGINT AUTO_INCREMENT, location_id INT, profile_id BIGINT, begin DATETIME NOT NULL, water_state TEXT, lure TEXT, bait TEXT, additive TEXT, weather TEXT, cordage TEXT, description TEXT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX location_id_idx (location_id), INDEX profile_id_idx (profile_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE profit_detail (id BIGINT AUTO_INCREMENT, time DATETIME NOT NULL, profit_id BIGINT, style_id BIGINT, fish_id BIGINT, qty FLOAT(18, 2) NOT NULL, UNIQUE INDEX unique_fish_style_per_profit_idx (profit_id, style_id, fish_id), INDEX profit_id_idx (profit_id), INDEX style_id_idx (style_id), INDEX fish_id_idx (fish_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE season (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE style (id BIGINT AUTO_INCREMENT, name VARCHAR(100) NOT NULL UNIQUE, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE friend ADD CONSTRAINT friend_source_profile_id_profile_id FOREIGN KEY (source_profile_id) REFERENCES profile(id);
ALTER TABLE location_version ADD CONSTRAINT location_version_id_location_id FOREIGN KEY (id) REFERENCES location(id) ON UPDATE CASCADE ON DELETE CASCADE;
ALTER TABLE location ADD CONSTRAINT location_location_relief_id_location_relief_id FOREIGN KEY (location_relief_id) REFERENCES location_relief(id);
ALTER TABLE location ADD CONSTRAINT location_location_fundus_id_location_fundus_id FOREIGN KEY (location_fundus_id) REFERENCES location_fundus(id);
ALTER TABLE location ADD CONSTRAINT location_location_flow_id_location_flow_id FOREIGN KEY (location_flow_id) REFERENCES location_flow(id);
ALTER TABLE location_season ADD CONSTRAINT location_season_season_id_season_id FOREIGN KEY (season_id) REFERENCES season(id);
ALTER TABLE location_season ADD CONSTRAINT location_season_location_id_location_id FOREIGN KEY (location_id) REFERENCES location(id);
ALTER TABLE profit ADD CONSTRAINT profit_profile_id_profile_id FOREIGN KEY (profile_id) REFERENCES profile(id);
ALTER TABLE profit ADD CONSTRAINT profit_location_id_location_id FOREIGN KEY (location_id) REFERENCES location(id);
ALTER TABLE profit_detail ADD CONSTRAINT profit_detail_style_id_style_id FOREIGN KEY (style_id) REFERENCES style(id);
ALTER TABLE profit_detail ADD CONSTRAINT profit_detail_profit_id_profit_id FOREIGN KEY (profit_id) REFERENCES profit(id);
ALTER TABLE profit_detail ADD CONSTRAINT profit_detail_fish_id_fish_id FOREIGN KEY (fish_id) REFERENCES fish(id);
